import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    fontWeight: 'bold',
    color: ({ status }) =>
      status === 'draft'
        ? theme.palette.text.primary
        : status === 'pending'
        ? '#FF8F00'
        : '#33D69F',
    background: ({ status }) =>
      status === 'draft'
        ? theme.palette.divider
        : status === 'pending'
        ? 'rgb(255, 143, 0, 0.1)'
        : 'rgb(51, 214, 159, 0.1)',
    marginLeft: 30,
    padding: '5px 25px',
