import React, { useState, useEffect } from 'react';
import FormField from './FormField/FormField';
import { useHistory } from 'react-router-dom';
import ItemsList from './ItemsList/ItemsList';
import { createInvoice, updateInvoice } from '../../actions/invoices';
import { useDispatch, useSelector } from 'react-redux';
import { toggleFormShow, handleCurrentId } from '../../actions/themeMode';
import {
  Container,
  Typography,
  MenuItem,
  Button,
  Paper,
  Modal,
} from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';
import useStyles from './styles';
import {
  ValidatorForm,
  SelectValidator,
} from 'react-material-ui-form-validator';

const Form = () => {
  const classes = useStyles();
  const history = useHistory();
  const dispatch = useDispatch();
  const showForm = useSelector((state) => state.GlobalState.showForm);
  const currentId = useSelector((state) => state.GlobalState.currentId);
  const invoice = useSelector((state) =>
    currentId
      ? state.GlobalState.invoices.find((invoice) => invoice._id === currentId)
      : null
  );

  const data = {
    createdAt: '',
    paymentDue: '',
    description: '',
    paymentTerms: '',
    clientName: '',
    clientEmail: '',
    status: 'pending',

    senderAddress: {
      street: '',
      city: '',
      postCode: '',
      country: '',
    },
    clientAddress: {
      street: '',
      city: '',
      postCode: '',
      country: '',
    },
    items: [],
    total: 0,
  };

  const [invoiceData, setInvocieData] = useState(data);

  const addItemField = () => {
    setInvocieData({
      ...invoiceData,
      items: [
        ...invoiceData.items,
        {
          name: '',
          quantity: 0,
          price: 0,
          total: 0,
        },
      ],
    });
  };

  const onDeleteItem = (id) => {
    const newItems = invoiceData.items.filter((item) => item._id !== id);
    setInvocieData({ ...invoiceData, items: newItems });
  };

  const handleChangeItemValue = (e, i) => {
    const inputName = e.target.name;
    let value = e.target.value;

    const newItems = invoiceData.items.map((item, idx) =>
      idx === invoiceData.items.indexOf(i)
        ? inputName === 'name'
          ? { ...item, name: value }
          : {
              ...item,
              [inputName]: Number(value),
              total:
                inputName === 'price'
                  ? value * item.quantity
                  : value * item.price,
            }
        : item
    );

    setInvocieData({
      ...invoiceData,
      items: newItems,
      total: newItems
        .map((item) => Number(item.total))
        .reduce((acc, currentValue) => acc + currentValue),
    });
  };

  const clear = () => {
    dispatch(handleCurrentId(null));
    setInvocieData(data);
  };

  const getDueDate = (created, paymentTerms) => {
    const date = new Date(created);
    const dueDate = new Date(date.setDate(date.getDate() + paymentTerms))
      .toISOString()
      .split('T')[0];

    return dueDate;
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    if (currentId) {
      dispatch(updateInvoice(currentId, invoiceData));
      dispatch(handleCurrentId(null));
      history.push('/');
    } else {
      dispatch(createInvoice(invoiceData));
    }

    dispatch(toggleFormShow());
    clear();
  };

  useEffect(() => {
    if (invoice) setInvocieData(invoice);

    if (!currentId) clear();

    // eslint-disable-next-line
  }, [currentId]);
  return (
    <Modal
      open={showForm}
      onClose={dispatch(toggleFormShow())}
      aria-labelledby="simple-modal-title"
      aria-describedby="simple-modal-description"
      // style={{ display: showForm ? 'block' : 'none' }}
      className={classes.root}
    >
      <Container className={classes.formContainer}>
        <Paper className={classes.paper}>
          <Typography variant="h4">
            {currentId ? `Edit #${currentId.slice(18)}` : 'New Invoice'}
          </Typography>

          <ValidatorForm onSubmit={onSubmit}>
            <div>
              <Typography
                variant="h5"
                className={classes.heading}
                color="primary"
              >
                Bill From
              </Typography>
              <div className={classes.label}>Street Address</div>

              <FormField
                name="senderAddress"
