import React from 'react';
import { Grid, TextField } from '@material-ui/core';

const Input = ({ name, value, label, type, half, handleChange }) => {
  return (
    <Grid xs={12} sm={half ? 6 : 12}>
      <TextField
        name={name}
        value={value}
        type={type}
        label={label}
        variant="outlined"
        fullWidth
        onChange={handleChange}
      />
    </Grid>
  );
};

export default Input;
