import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    margin: '20px 0 0',
    padding: '20px 10px',
  },
  btn: {
    marginRight: 8,
    borderRadius: '25px',
    fontWeight: '700',
    fontSize: '12px',
    [theme.breakpoints.down('xs')]: {
      marginRight: 3,
      fontSize: '8px',
      fontWeight: '500',
      padding: 5,
    },
  },
}));

export default useStyles;
