import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: '5px',
  },
  error: {
    border: `1px solid ${theme.palette.error.main}`,
  },
}));

export default useStyles;
