export const data = {
  createdAt: '',
  paymentDue: '',
  description: '',
  paymentTerms: '',
  clientName: '',
  clientEmail: '',
  status: 'pending',

  senderAddress: {
    street: '',
    city: '',
    postCode: '',
    country: '',
  },
  clientAddress: {
    street: '',
    city: '',
    postCode: '',
    country: '',
  },
  items: [],
  total: 0,
};

export const getTotal = (items) =>
  items.length > 0
    ? items
        .map((item) => Number(item.total))
        .reduce((acc, currentValue) => acc + currentValue)
    : 0;

export const getDueDate = (created, paymentTerms) => {
  const date = new Date(created);
  const dueDate = new Date(date.setDate(date.getDate() + paymentTerms))
    .toISOString()
    .split('T')[0];

  return dueDate;
};
