import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
  },
  paper: {
    textAlign: 'center',
    padding: '30px',
  },
  avatar: {
    margin: 8,
    backgroundColor: theme.palette.secondary.main,
  },
}));

export default useStyles;
