import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    fontWeight: '500',
    textAlgin: 'center',
    color: ({ status }) =>
      status === 'draft'
        ? theme.palette.text.primary
        : status === 'pending'
        ? '#FF8F00'
        : '#33D69F',
    background: ({ invoice }) =>
      status === 'draft'
        ? theme.palette.divider
        : status === 'pending'
        ? 'rgb(255, 143, 0, 0.1)'
        : 'rgb(51, 214, 159, 0.1)',
    padding: '10px 15px',
    borderRadius: '6px',
    display: 'list-item',
    listStyleType: 'disc',
    listStylePosition: 'inside',
    transform: 'translate(-10px, -5px)',
    [theme.breakpoints.down('xs')]: {
      padding: '10px',
    },
  },
}));

export default useStyles;
