import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: 20,
    padding: '35px 10px',
    borderRadius: 8,
    fontSize: '12px',
    fontWeight: '500',
    lineHeight: '15px',

    [theme.breakpoints.down('sm')]: {
      padding: '25px',
    },
  },

  boldFont: {
    fontWeight: 700,
  },
  lightFont: {
    color: theme.palette.text.secondary,
    fontWeight: 500,
  },

  icon: {
    cursor: 'pointer',
  },
}));

export default useStyles;
