import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100vw',
    background: 'rgba(0, 0 , 0 , 0.4)',
    overflow: 'scroll',
    fontSize: '12px',
  },
}));

export default useStyles;
